INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'004.11','UNIFORMIZAÇÃO DO ENTENDIMENTO JURÍDICO (análise e fixação de interpretação da Constituição, leis, tratados e demais atos normativos, a serem seguidos, de modo uniforme, pelo órgão e entidade, quando não houver orientação normativa superior)','Condicional "Enquanto vigora" convencionado para 15 anos no corrente, que foi considerado como tempo médio de vigência.','004.11 00411 uniformizacao do entendimento juridico analise e fixacao de interpretacao da constituicao leis tratados e demais atos normativos a serem seguidos de modo uniforme pelo orgao e entidade quando nao houver orientacao normativa superior condicional enquanto vigora convencionado para 15 anos no corrente que foi considerado como tempo medio de vigencia','S','5','15','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'004.12','ANÁLISE DOS INSTRUMENTOS ADMINISTRATIVOS (exame e à análise prévia ou conclusiva de textos de editais de licitação, de contratos e de instrumentos congêneres a serem publicados e celebrados pelo órgão e entidade)','Condicional "Até o término da análise" convencionado para 1 ano no corrente, que foi considerado como tempo médio para finalização da análise dos instrumentos administrativos submetidos.','004.12 00412 analise dos instrumentos administrativos exame e a analise previa ou conclusiva de textos de editais de licitacao de contratos e de instrumentos congeneres a serem publicados e celebrados pelo orgao e entidade condicional ate o termino da analise convencionado para 1 ano no corrente que foi considerado como tempo medio para finalizacao da analise dos instrumentos administrativos submetidos','S','5','1','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'004.2','ATUAÇÃO EM CONTENCIOSO','Nas subdivisões deste descritor classificam-se documentos referentes à atuação contenciosa judicial e administrativa, nas hipóteses de contestação, conflito ou litígio, exercida pela assessoria ou consultoria jurídica do órgão e entidade.','004.2 0042 atuacao em contencioso nas subdivisoes deste descritor classificam-se documentos referentes a atuacao contenciosa judicial e administrativa nas hipoteses de contestacao conflito ou litigio exercida pela assessoria ou consultoria juridica do orgao e entidade','S',NULL,NULL,NULL,'S','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'004.21','REPRESENTAÇÃO EXTRAJUDICIAL (solução extrajudicial dos litígios, visando à composição entre as partes em conflito de interesses, por meio de mediação, conciliação, arbitragem e demais técnicas de composição e administração de conflitos)','Condicional "Até a solução do litígio" convencionado para 5 anos, que foi considerado como tempo médio para a solução do litígio.','004.21 00421 representacao extrajudicial solucao extrajudicial dos litigios visando a composicao entre as partes em conflito de interesses por meio de mediacao conciliacao arbitragem e demais tecnicas de composicao e administracao de conflitos condicional ate a solucao do litigio convencionado para 5 anos que foi considerado como tempo medio para a solucao do litigio','S','5','5','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'004.22','REPRESENTAÇÃO JUDICIAL (ações defendidas pela assessoria ou consultoria jurídica em processos administ. e judiciais, perante todas as instâncias, ordinárias ou extraordinárias, para propiciar a efetiva defesa dos interesses do órgão e entidade)','Condicional "Até o trânsito em julgado" convencionado para 5 anos no corrente, que foi considerado como tempo médio para o trânsito em julgado de processo judicial correspondente à representação judicial.','004.22 00422 representacao judicial acoes defendidas pela assessoria ou consultoria juridica em processos administ e judiciais perante todas as instancias ordinarias ou extraordinarias para propiciar a efetiva defesa dos interesses do orgao e entidade condicional ate o transito em julgado convencionado para 5 anos no corrente que foi considerado como tempo medio para o transito em julgado de processo judicial correspondente a representacao judicial','S','5','1','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'005','PARTICIPAÇÃO EM ÓRGÃOS COLEGIADOS','Nas subdivisões deste descritor classificam-se documentos referentes à organização e ao funcionamento de comissões, conselhos, comitês, juntas e grupos de trabalho, criados pelo próprio órgão e entidade ou por outros órgãos de deliberação coletiva, que contem com a participação de servidores da instituição.\nQuanto à atuação de comissões, conselhos, comitês, juntas e grupos de trabalho referentes a uma atividade em particular, classificar no código específico.','005 005 participacao em orgaos colegiados nas subdivisoes deste descritor classificam-se documentos referentes a organizacao e ao funcionamento de comissoes conselhos comites juntas e grupos de trabalho criados pelo proprio orgao e entidade ou por outros orgaos de deliberacao coletiva que contem com a participacao de servidores da instituicao quanto a atuacao de comissoes conselhos comites juntas e grupos de trabalho referentes a uma atividade em particular classificar no codigo especifico','S',NULL,NULL,NULL,'S','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'005.1','CRIAÇÃO E ATUAÇÃO (implantação dos órgãos colegiados e aos registros das deliberações definidas nas reuniões, tais como: ato de instituição, regras para atuação, designação e substituição de membros, resoluções, atas e relatórios)','','005.1 0051 criacao e atuacao implantacao dos orgaos colegiados e aos registros das deliberacoes definidas nas reunioes tais como: ato de instituicao regras para atuacao designacao e substituicao de membros resolucoes atas e relatorios','S','5','4','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'005.2','OPERACIONALIZAÇÃO DE REUNIÕES (organização das reuniões dos órgãos colegiados, bem como aqueles referentes ao agendamento, convocação, pauta e lista de participantes)','','005.2 0052 operacionalizacao de reunioes organizacao das reunioes dos orgaos colegiados bem como aqueles referentes ao agendamento convocacao pauta e lista de participantes','S',NULL,'2','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'010','ORGANIZAÇÃO E FUNCIONAMENTO','Esta subclasse contempla documentos referentes à definição e alteração das políticas institucionais, à criação e modificação das estruturas organizacionais e aos registros que garantam a existência do órgão e entidade como pessoa jurídica e a sua atuação no meio público, privado, com o terceiro setor e com o cidadão, bem como aqueles referentes ao planejamento e acompanhamento das ações institucionais, da gestão ambiental e da comunicação social.','010 010 organizacao e funcionamento esta subclasse contempla documentos referentes a definicao e alteracao das politicas institucionais a criacao e modificacao das estruturas organizacionais e aos registros que garantam a existencia do orgao e entidade como pessoa juridica e a sua atuacao no meio publico privado com o terceiro setor e com o cidadao bem como aqueles referentes ao planejamento e acompanhamento das acoes institucionais da gestao ambiental e da comunicacao social','S',NULL,NULL,NULL,'S','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'010.01','NORMATIZAÇÃO. REGULAMENTAÇÃO (determinações legais, atos e instruções normativas, procedimentos operacionais e decisões de caráter geral sobre a organização e funcionamento do órgão e entidade, bem como os boletins administrativos e de serviço)','Quanto aos boletins de pessoal, classificar no código 020.01.\nQuanto à publicação de matérias em diários oficiais e em periódicos de grande circulação, classificar no código 069.3.\n\nCondicional "Enquanto vigora" convencionado para 15 anos no corrente, que foi considerado como tempo médio de vigência.','010.01 01001 normatizacao regulamentacao determinacoes legais atos e instrucoes normativas procedimentos operacionais e decisoes de carater geral sobre a organizacao e funcionamento do orgao e entidade bem como os boletins administrativos e de servico quanto aos boletins de pessoal classificar no codigo 02001 quanto a publicacao de materias em diarios oficiais e em periodicos de grande circulacao classificar no codigo 0693 condicional enquanto vigora convencionado para 15 anos no corrente que foi considerado como tempo medio de vigencia','S','5','15','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'011','ORGANIZAÇÃO ADMINISTRATIVA (estudos para definição da estrutura e das atribuições, mudanças estratégicas e estruturais, reformas administrativas ou de processos de modernização, com impacto na forma de fusão, privatização, reestatização ou extinção)','Eliminar, após 2 anos, os estudos preliminares ou as versões não implementadas das mudanças estruturais\n\nCondicional "Enquanto vigora" convencionado para 20 anos no corrente, que foi considerado como tempo médio de vigência.','011 011 organizacao administrativa estudos para definicao da estrutura e das atribuicoes mudancas estrategicas e estruturais reformas administrativas ou de processos de modernizacao com impacto na forma de fusao privatizacao reestatizacao ou extincao eliminar apos 2 anos os estudos preliminares ou as versoes nao implementadas das mudancas estruturais condicional enquanto vigora convencionado para 20 anos no corrente que foi considerado como tempo medio de vigencia','S','5','20','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'012','HABILITAÇÃO JURÍDICA E REGULARIZAÇÃO FISCAL (inscrição, baixa e cancelamento nos órgãos competentes, tais como: cadastro bancário, registros de inscrição no CNPJ, no Sisbacen, no SPC, no Cadin','Condicional "Enquanto vigora" convencionado para 5 anos no corrente, que foi considerado como tempo médio de vigência.','012 012 habilitacao juridica e regularizacao fiscal inscricao baixa e cancelamento nos orgaos competentes tais como: cadastro bancario registros de inscricao no cnpj no sisbacen no spc no cadin condicional enquanto vigora convencionado para 5 anos no corrente que foi considerado como tempo medio de vigencia','S','2','5','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'013','COORDENAÇÃO E GESTÃO DE REUNIÕES','Nas subdivisões deste descritor classificam-se documentos relativos às atividades necessárias para a realização de assembleias, audiências, despachos e reuniões, gerais e setoriais, do órgão e entidade, bem como aqueles referentes ao registro de suas deliberações.\nQuanto aos documentos de assembleias, audiências, despachos e reuniões produzidos pelas diferentes comissões instituídas no âmbito do órgão e entidade, classificar no código específico.','013 013 coordenacao e gestao de reunioes nas subdivisoes deste descritor classificam-se documentos relativos as atividades necessarias para a realizacao de assembleias audiencias despachos e reunioes gerais e setoriais do orgao e entidade bem como aqueles referentes ao registro de suas deliberacoes quanto aos documentos de assembleias audiencias despachos e reunioes produzidos pelas diferentes comissoes instituidas no ambito do orgao e entidade classificar no codigo especifico','S',NULL,NULL,NULL,'S','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'013.1','OPERACIONALIZAÇÃO (organização das reuniões do órgão e entidade, bem como aqueles referentes ao agendamento, convocação, pauta e lista de participantes)','','013.1 0131 operacionalizacao organizacao das reunioes do orgao e entidade bem como aqueles referentes ao agendamento convocacao pauta e lista de participantes','S',NULL,'2','E','N','2');
