INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'014.1','NOMEAÇÃO E ATUAÇÃO DA COMISSÃO ELEITORAL (constituição e atividades da comissão eleitoral, das mesas de votação e dos fiscais, indicação dos membros, regimento eleitoral, convocação, pauta, lista de participantes e registros e atas das reuniões)','Incluem-se documentos referentes à constituição e às atividades da comissão eleitoral, das mesas de votação e dos fiscais, tais como: indicação dos membros, regimento eleitoral, convocação, pauta, lista de participantes e registros e atas das reuniões.\n\nCondicional "Até a homologação do evento" convencionado para 2 anos no corrente, que foi considerado como tempo médio de homologação de evento.','014.1 0141 nomeacao e atuacao da comissao eleitoral constituicao e atividades da comissao eleitoral das mesas de votacao e dos fiscais indicacao dos membros regimento eleitoral convocacao pauta lista de participantes e registros e atas das reunioes incluem-se documentos referentes a constituicao e as atividades da comissao eleitoral das mesas de votacao e dos fiscais tais como: indicacao dos membros regimento eleitoral convocacao pauta lista de participantes e registros e atas das reunioes condicional ate a homologacao do evento convencionado para 2 anos no corrente que foi considerado como tempo medio de homologacao de evento','S','1','2','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'014.2','INSCRIÇÃO (Definição do calend. eleitoral, à inscrição de candidatos e divulg. das ativid. eletivas e dos candidatos, tais como: cronograma, cópia de doc. pessoais dos integrantes das chapas concorrentes, propag. eleitoral e pedidos de impug.)','Condicional "Até a homologação do evento" convencionado para 2 anos no corrente, que foi considerado como tempo médio de homologação de evento.','014.2 0142 inscricao definicao do calend eleitoral a inscricao de candidatos e divulg das ativid eletivas e dos candidatos tais como: cronograma copia de doc pessoais dos integrantes das chapas concorrentes propag eleitoral e pedidos de impug condicional ate a homologacao do evento convencionado para 2 anos no corrente que foi considerado como tempo medio de homologacao de evento','S','1','2','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'014.3','VOTAÇÃO (apuração do processo eletivo, tais como: relação de eleitores habilitados, cédulas de votação e contagem de votos)','Eliminar, após 2 anos da homologação do evento, as cédulas de votação.\n\nIncluem-se documentos referentes à apuração do processo eletivo, tais como: relação de eleitores habilitados, cédulas de votação e contagem de votos.\n\nCondicional "Até a homologação do evento" convencionado para 2 anos no corrente, que foi considerado como tempo médio de homologação de evento.','014.3 0143 votacao apuracao do processo eletivo tais como: relacao de eleitores habilitados cedulas de votacao e contagem de votos eliminar apos 2 anos da homologacao do evento as cedulas de votacao incluem-se documentos referentes a apuracao do processo eletivo tais como: relacao de eleitores habilitados cedulas de votacao e contagem de votos condicional ate a homologacao do evento convencionado para 2 anos no corrente que foi considerado como tempo medio de homologacao de evento','S','1','2','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'014.4','DIVULGAÇÃO DOS RESULTADOS E INTERPOSIÇÃO DE RECURSOS (resultados finais da eleição realizada e à divulgação dos candidatos eleitos, bem como aqueles referentes aos recursos impetrados)','Aguardar o término da ação, no caso de interposição de recursos.\n\nEliminar, após 2 anos da homologação do evento, os documentos de recursos indeferidos.\n\nIncluem-se documentos referentes aos resultados finais da eleição realizada e à divulgação dos candidatos eleitos, bem como aqueles referentes aos recursos impetrados.\n\nCondicional "Até a homologação do evento" convencionado para 2 anos no corrente, que foi considerado como tempo médio de homologação de evento.','014.4 0144 divulgacao dos resultados e interposicao de recursos resultados finais da eleicao realizada e a divulgacao dos candidatos eleitos bem como aqueles referentes aos recursos impetrados aguardar o termino da acao no caso de interposicao de recursos eliminar apos 2 anos da homologacao do evento os documentos de recursos indeferidos incluem-se documentos referentes aos resultados finais da eleicao realizada e a divulgacao dos candidatos eleitos bem como aqueles referentes aos recursos impetrados condicional ate a homologacao do evento convencionado para 2 anos no corrente que foi considerado como tempo medio de homologacao de evento','S','1','2','G','N','2');
