INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'015.3','AVALIAÇÃO DA GESTÃO INSTITUCIONAL','Eliminar, após 2 anos, os documentos cujas informações estejam recapituladas ou consolidadas em outros.\n\nNas subdivisões deste descritor classificam-se documentos referentes às ações de autoavaliação, para verificação do desempenho do órgão e entidade, visando o controle da qualidade e a melhoria na prestação do serviço público.','015.3 0153 avaliacao da gestao institucional eliminar apos 2 anos os documentos cujas informacoes estejam recapituladas ou consolidadas em outros nas subdivisoes deste descritor classificam-se documentos referentes as acoes de autoavaliacao para verificacao do desempenho do orgao e entidade visando o controle da qualidade e a melhoria na prestacao do servico publico','S',NULL,NULL,NULL,'S','2');
