INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'024.5','CONCESSÃO DE ESTÁGIOS E BOLSAS PARA ESTUDANTES','Nas subdivisões deste descritor classificam-se documentos referentes à oferta de estágios, remunerados ou não, e à concessão de bolsas para estudantes (residentes médicos, multiprofissionais e estagiários), por parte do órgão e entidade.','024.5 0245 concessao de estagios e bolsas para estudantes nas subdivisoes deste descritor classificam-se documentos referentes a oferta de estagios remunerados ou nao e a concessao de bolsas para estudantes residentes medicos multiprofissionais e estagiarios por parte do orgao e entidade','S',NULL,NULL,NULL,'S','2');
