INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'025.3','SEGURANÇA DO TRABALHO. PREVENÇÃO DE ACIDENTES DE TRABALHO','Nas subdivisões deste descritor classificam-se documentos referentes às medidas preventivas relacionadas ao ambiente de trabalho, visando à redução de acidentes e doenças ocupacionais.\n\nQuanto ao pagamento dos adicionais de periculosidade, insalubridade e atividade penosa, classificar nos códigos 023.163, 023.164 e 023.165, respectivamente.','025.3 0253 seguranca do trabalho prevencao de acidentes de trabalho nas subdivisoes deste descritor classificam-se documentos referentes as medidas preventivas relacionadas ao ambiente de trabalho visando a reducao de acidentes e doencas ocupacionais quanto ao pagamento dos adicionais de periculosidade insalubridade e atividade penosa classificar nos codigos 023163 023164 e 023165 respectivamente','S',NULL,NULL,NULL,'S','2');
