INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'028','CUMPRIMENTO DE MISSÕES E VIAGENS A SERVIÇO (afastamento do servidor para o cumprimento de missões e a realização de viagens a serviço, no país e no exterior, e para servir em organismo internacional de que o Brasil participe ou com o qual coopere)','Quanto ao planejamento, acompanhamento, execução, avaliação e relatório técnico, classificar no código específico relativo ao objeto da missão e da viagem a serviço.\n\nQuanto aos afastamentos referentes à concessão de direitos e vantagens (suspensão de contrato de trabalho, depor, exercer mandato legislativo, servir a Justiça Eleitoral, servir como jurado, participar em programas de pós-graduação stricto sensu e de pós-doutorado e em estudos, no país e no exterior), classificar no código 023.4.','028 028 cumprimento de missoes e viagens a servico afastamento do servidor para o cumprimento de missoes e a realizacao de viagens a servico no pais e no exterior e para servir em organismo internacional de que o brasil participe ou com o qual coopere quanto ao planejamento acompanhamento execucao avaliacao e relatorio tecnico classificar no codigo especifico relativo ao objeto da missao e da viagem a servico quanto aos afastamentos referentes a concessao de direitos e vantagens suspensao de contrato de trabalho depor exercer mandato legislativo servir a justica eleitoral servir como jurado participar em programas de pos-graduacao stricto sensu e de pos-doutorado e em estudos no pais e no exterior classificar no codigo 0234','S',NULL,NULL,NULL,'S','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'028.1','NO PAÍS','Nas subdivisões deste descritor classificam-se documentos referentes ao afastamento do servidor para o cumprimento de missões e a realização de viagens no país, podendo ser: com ônus (quando implicarem no direito à concessão de passagens e diárias e ao recebimento de vencimentos ou salários e demais vantagens) e com ônus limitado (quando implicarem, apenas, no direito ao recebimento de vencimentos e salários e demais vantagens).','028.1 0281 no pais nas subdivisoes deste descritor classificam-se documentos referentes ao afastamento do servidor para o cumprimento de missoes e a realizacao de viagens no pais podendo ser: com onus quando implicarem no direito a concessao de passagens e diarias e ao recebimento de vencimentos ou salarios e demais vantagens e com onus limitado quando implicarem apenas no direito ao recebimento de vencimentos e salarios e demais vantagens','S',NULL,NULL,NULL,'S','2');
