INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'028.2','NO EXTERIOR (afastamento do país do servidor para o cumprimento de missões e a realização de viagens ao exterior, podendo ser: com ônus, com ônus limitado e sem ônus)','Afastamento do país do servidor para o cumprimento de missões e a realização de viagens ao exterior, podendo ser: com ônus (direito à concessão de passagens e diárias e ao recebimento de vencimentos ou salários e demais vantagens), com ônus limitado (quando implicarem, apenas, no direito ao recebimento de vencimentos e salários e demais vantagens) e sem ônus (quando implicarem a perda total dos vencimentos ou salários e demais vantagens, não acarretando qualquer despesa para o órgão e entidade).','028.2 0282 no exterior afastamento do pais do servidor para o cumprimento de missoes e a realizacao de viagens ao exterior podendo ser: com onus com onus limitado e sem onus afastamento do pais do servidor para o cumprimento de missoes e a realizacao de viagens ao exterior podendo ser: com onus direito a concessao de passagens e diarias e ao recebimento de vencimentos ou salarios e demais vantagens com onus limitado quando implicarem apenas no direito ao recebimento de vencimentos e salarios e demais vantagens e sem onus quando implicarem a perda total dos vencimentos ou salarios e demais vantagens nao acarretando qualquer despesa para o orgao e entidade','S',NULL,NULL,NULL,'S','2');
