INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'029.24','AVALIAÇÃO DO PROGRAMA (avaliações periódicas e permanentes do programa, tais como: laudos técnicos, estabelecimento de indicadores, estudos de ofertas e demandas e relatórios)','Eliminar, após 2 anos, os documentos cujas informações encontram-se recapituladas ou consolidadas em outros.','029.24 02924 avaliacao do programa avaliacoes periodicas e permanentes do programa tais como: laudos tecnicos estabelecimento de indicadores estudos de ofertas e demandas e relatorios eliminar apos 2 anos os documentos cujas informacoes encontram-se recapituladas ou consolidadas em outros','S','5','5','G','N','2');
