INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'029.4','DELEGAÇÃO DE COMPETÊNCIA E PROCURAÇÃO (autorização/delegação que autoridade responsável pode conferir a um servidor para tratar de matéria de competência exclusiva, tais como: ordenação de despesas, decisões de recursos e edição de atos normativo)','Autorização ou delegação que a autoridade administrativa responsável pelo órgão e entidade pode conferir a um servidor para tratar de matéria de competência exclusiva, tais como: ordenação de despesas, decisões de recursos e casos de edição de atos de caráter normativo.\n\nUtilizar os prazos dos documentos financeiros para os documentos referentes aos ordenadores de despesas.\n\nCondicional "Enquanto vigora" convencionado para 5 anos no corrente, que foi considerado como tempo médio de vigência.','029.4 0294 delegacao de competencia e procuracao autorizacao delegacao que autoridade responsavel pode conferir a um servidor para tratar de materia de competencia exclusiva tais como: ordenacao de despesas decisoes de recursos e edicao de atos normativo autorizacao ou delegacao que a autoridade administrativa responsavel pelo orgao e entidade pode conferir a um servidor para tratar de materia de competencia exclusiva tais como: ordenacao de despesas decisoes de recursos e casos de edicao de atos de carater normativo utilizar os prazos dos documentos financeiros para os documentos referentes aos ordenadores de despesas condicional enquanto vigora convencionado para 5 anos no corrente que foi considerado como tempo medio de vigencia','S','5','5','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'029.5','CONTRATAÇÃO DE SERVIÇOS PROFISSIONAIS TRANSITÓRIOS (planejamento, divulgação, seleção do fornecedor, designação do gestor e dos fiscais, fiscalização, avaliação e aferição dos resultados e demais documentos comprobatórios da prestação dos serviços)','A contratação poderá ocorrer nas modalidades de licitação, dispensa e inexigibilidade de licitação.\n\nQuanto aos documentos referentes às pessoas físicas contratadas (autônomos e colaboradores), que deverão integrar o assentamento funcional, classificar no código 020.12.\n\nCondicional "Até a aprovação das contas pelo Tribunal de Contas" convencionado para 5 anos no corrente e "5 anos a contar da aprovação das contas pelo Tribunal de Contas" para 15 anos no intermediário, somando-se 20 anos.','029.5 0295 contratacao de servicos profissionais transitorios planejamento divulgacao selecao do fornecedor designacao do gestor e dos fiscais fiscalizacao avaliacao e afericao dos resultados e demais documentos comprobatorios da prestacao dos servicos a contratacao podera ocorrer nas modalidades de licitacao dispensa e inexigibilidade de licitacao quanto aos documentos referentes as pessoas fisicas contratadas autonomos e colaboradores que deverao integrar o assentamento funcional classificar no codigo 02012 condicional ate a aprovacao das contas pelo tribunal de contas convencionado para 5 anos no corrente e 5 anos a contar da aprovacao das contas pelo tribunal de contas para 15 anos no intermediario somando-se 20 anos','S','15','5','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'029.6','PETIÇÃO DE DIREITOS (Incluem-se requerimentos feitos pelo servidor em defesa de seus direitos ou interesses legítimos e pedidos de interposição de reconsideração ou de recurso)','Condicional "Até a solução da interposição de pedido de reconsideração ou de recurso" convencionado para 1 ano no correte, superior aos 15 dias previstos pelo direito de petição do art. 5º, XXXIV, a, da Constituição da República, ou igualmente superior ao prazo de decisão de até trinta dias após concluída a instrução de processo administrativo pela Administração.','029.6 0296 peticao de direitos incluem-se requerimentos feitos pelo servidor em defesa de seus direitos ou interesses legitimos e pedidos de interposicao de reconsideracao ou de recurso condicional ate a solucao da interposicao de pedido de reconsideracao ou de recurso convencionado para 1 ano no correte superior aos 15 dias previstos pelo direito de peticao do art 5º xxxiv a da constituicao da republica ou igualmente superior ao prazo de decisao de ate trinta dias apos concluida a instrucao de processo administrativo pela administracao','S','5','1','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'030','GESTÃO DE MATERIAIS (aquisição, movimentação, alienação, baixa e inventário de material permanente e de consumo, bem como aqueles referentes à contratação de prestação de serviços e à execução de serviços de instalação e manutenção)','Aquisição, movimentação, alienação, baixa e inventário de material permanente (mobiliário, equipamentos, utensílios, aparelhos, ferramentas, máquinas, instrumentos técnicos e obras de arte) e de consumo (expediente, limpeza, manutenção, alimentação e abastecimento de veículos, medicamentos, uniformes, peças de reposição, matérias-primas e cobaias para uso científico), bem como aqueles referentes à contratação de prestação de serviços e à execução de serviços de instalação e manutenção.','030 030 gestao de materiais aquisicao movimentacao alienacao baixa e inventario de material permanente e de consumo bem como aqueles referentes a contratacao de prestacao de servicos e a execucao de servicos de instalacao e manutencao aquisicao movimentacao alienacao baixa e inventario de material permanente mobiliario equipamentos utensilios aparelhos ferramentas maquinas instrumentos tecnicos e obras de arte e de consumo expediente limpeza manutencao alimentacao e abastecimento de veiculos medicamentos uniformes pecas de reposicao materias-primas e cobaias para uso cientifico bem como aqueles referentes a contratacao de prestacao de servicos e a execucao de servicos de instalacao e manutencao','S',NULL,NULL,NULL,'S','2');
