INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'031','AQUISIÇÃO E INCORPORAÇÃO','Eliminar, após 2 anos, os documentos referentes às ações de aquisição e incorporação não efetivadas.\n\nNas subdivisões deste descritor classificam-se documentos referentes à aquisição e incorporação de material permanente, de consumo e de matéria-prima e insumo.','031 031 aquisicao e incorporacao eliminar apos 2 anos os documentos referentes as acoes de aquisicao e incorporacao nao efetivadas nas subdivisoes deste descritor classificam-se documentos referentes a aquisicao e incorporacao de material permanente de consumo e de materia-prima e insumo','S',NULL,NULL,NULL,'S','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'031.1','COMPRA','Nas subdivisões deste descritor classificam-se documentos referentes à aquisição definitiva de material permanente e de consumo.\n\nNota: A compra poderá ocorrer nas modalidades de licitação (convite, tomada de preços, concorrência, leilão, concurso e pregão), dispensa de licitação e inexigibilidade de licitação.','031.1 0311 compra nas subdivisoes deste descritor classificam-se documentos referentes a aquisicao definitiva de material permanente e de consumo nota: a compra podera ocorrer nas modalidades de licitacao convite tomada de precos concorrencia leilao concurso e pregao dispensa de licitacao e inexigibilidade de licitacao','S',NULL,NULL,NULL,'S','2');
