INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'032','MOVIMENTAÇÃO DE MATERIAL','Nas subdivisões deste descritor classificam-se documentos referentes à movimentação de material permanente e de consumo, que envolve as atividades de controle, segurança e proteção do armazenamento, do deslocamento, da distribuição, da previsão de consumo e da reposição de estoque.','032 032 movimentacao de material nas subdivisoes deste descritor classificam-se documentos referentes a movimentacao de material permanente e de consumo que envolve as atividades de controle seguranca e protecao do armazenamento do deslocamento da distribuicao da previsao de consumo e da reposicao de estoque','S',NULL,NULL,NULL,'S','2');
