INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'032.2','AUTORIZAÇÃO DE ENTRADA E SAÍDA DE MATERIAL (autorização de entrada e saída de material, bem como aqueles referentes à armazenagem no decorrer do procedimento de desembaraço aduaneiro)','Quanto ao empréstimo e devolução de material permanente disponibilizado aos servidores, classificar no código 039.2.\n\nObservar os prazos da legislação em vigor para os documentos referentes a produtos e insumos químicos e outras substâncias entorpecentes.','032.2 0322 autorizacao de entrada e saida de material autorizacao de entrada e saida de material bem como aqueles referentes a armazenagem no decorrer do procedimento de desembaraco aduaneiro quanto ao emprestimo e devolucao de material permanente disponibilizado aos servidores classificar no codigo 0392 observar os prazos da legislacao em vigor para os documentos referentes a produtos e insumos quimicos e outras substancias entorpecentes','S',NULL,'1','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'032.3','RECOLHIMENTO DE MATERIAL INSERVÍVEL AO DEPÓSITO (recolhimento de material inservível e de sucatas ao depósito do órgão e entidade)','Quanto à coleta seletiva solidária e à reciclagem de resíduos descartáveis, classificar no código 017.1.\n\nQuanto à alienação definitiva, por desfazimento, de material permanente e de consumo em razão de serem considerados inservíveis e irrecuperáveis, classificar nas subdivisões do código 033.4.\n\nObservar os prazos da legislação em vigor para os documentos referentes a produtos e insumos químicos e outras substâncias entorpecentes.','032.3 0323 recolhimento de material inservivel ao deposito recolhimento de material inservivel e de sucatas ao deposito do orgao e entidade quanto a coleta seletiva solidaria e a reciclagem de residuos descartaveis classificar no codigo 0171 quanto a alienacao definitiva por desfazimento de material permanente e de consumo em razao de serem considerados inserviveis e irrecuperaveis classificar nas subdivisoes do codigo 0334 observar os prazos da legislacao em vigor para os documentos referentes a produtos e insumos quimicos e outras substancias entorpecentes','S',NULL,'2','E','N','2');
