INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'033.4','DESFAZIMENTO','Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de material permanente e de consumo em razão de serem considerados inservíveis e irrecuperáveis, ocorrendo por meio de inutilização, eliminação ou destruição.\n\nQuanto ao monitoramento e avaliação dos procedimentos de controle e preservação ambiental externa, classificar no código 017.2.\n\nQuanto ao recolhimento de material inservível e de sucatas ao depósito, classificar no código 032.3.','033.4 0334 desfazimento nas subdivisoes deste descritor classificam-se documentos referentes a alienacao definitiva de material permanente e de consumo em razao de serem considerados inserviveis e irrecuperaveis ocorrendo por meio de inutilizacao eliminacao ou destruicao quanto ao monitoramento e avaliacao dos procedimentos de controle e preservacao ambiental externa classificar no codigo 0172 quanto ao recolhimento de material inservivel e de sucatas ao deposito classificar no codigo 0323','S',NULL,NULL,NULL,'S','2');
