INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'036','CONTROLE DE MATERIAIS','Nas subdivisões deste descritor classificam-se documentos referentes ao acompanhamento e controle de bens materiais, que subsidiam a elaboração dos inventários.\n\nQuanto à requisição e distribuição de material e à reposição e controle de estoque, classificar no código 032.1.\n\nQuanto ao inventário de acervos bibliográfico e museológico, classificar no código 062.3.','036 036 controle de materiais nas subdivisoes deste descritor classificam-se documentos referentes ao acompanhamento e controle de bens materiais que subsidiam a elaboracao dos inventarios quanto a requisicao e distribuicao de material e a reposicao e controle de estoque classificar no codigo 0321 quanto ao inventario de acervos bibliografico e museologico classificar no codigo 0623','S',NULL,NULL,NULL,'S','2');
