INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'039.11','CRIAÇÃO E ATUAÇÃO DE GRUPOS DE TRABALHO (criação de grupos de trabalho e comissões de racionalização do uso de material, registros das deliberações e tomadas de decisão definidas nas reuniões)','Incluem-se documentos referentes à criação de grupos de trabalho e comissões de racionalização do uso de material, aos registros das deliberações e às tomadas de decisão definidas nas reuniões, tais como: ato de instituição, regras para atuação, designação e substituição de membros, resoluções, atas e relatórios.','039.11 03911 criacao e atuacao de grupos de trabalho criacao de grupos de trabalho e comissoes de racionalizacao do uso de material registros das deliberacoes e tomadas de decisao definidas nas reunioes incluem-se documentos referentes a criacao de grupos de trabalho e comissoes de racionalizacao do uso de material aos registros das deliberacoes e as tomadas de decisao definidas nas reunioes tais como: ato de instituicao regras para atuacao designacao e substituicao de membros resolucoes atas e relatorios','S','5','4','G','N','2');
