INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'045','CONTRATAÇÃO DE PRESTAÇÃO DE SERVIÇOS (planejamento, divulgação, seleção do fornecedor, designação do gestor e dos fiscais, fiscalização, avaliação e aferição dos resultados e demais documentos comprobatórios da prestação dos serviços)','Contratação de empresas para a realização de fornecimento de serviços públicos essenciais, de manutenção e reparo das instalações, de execução de obras, de vigilância patrimonial, de abastecimento e manutenção de veículos e de assistência veterinária e adestramento de animais a serem prestados sob o regime de execução indireta.\n\nA contratação poderá ocorrer nas modalidades de licitação (convite, tomada de preços, concorrência, leilão, concurso e pregão), dispensa e inexigibilidade de licitação.','045 045 contratacao de prestacao de servicos planejamento divulgacao selecao do fornecedor designacao do gestor e dos fiscais fiscalizacao avaliacao e afericao dos resultados e demais documentos comprobatorios da prestacao dos servicos contratacao de empresas para a realizacao de fornecimento de servicos publicos essenciais de manutencao e reparo das instalacoes de execucao de obras de vigilancia patrimonial de abastecimento e manutencao de veiculos e de assistencia veterinaria e adestramento de animais a serem prestados sob o regime de execucao indireta a contratacao podera ocorrer nas modalidades de licitacao convite tomada de precos concorrencia leilao concurso e pregao dispensa e inexigibilidade de licitacao','S',NULL,NULL,NULL,'S','2');
