INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'069.11','ARRANJO E DESCRIÇÃO (metodologia adotada para o desenvolvimento das atividades de identificação, arranjo e descrição dos documentos arquivísticos de guarda permanente)','Eliminar, 2 anos após a conclusão da organização, as versões preliminares das planilhas e dos estudos de apoio.\n\nIncluem-se documentos referentes à metodologia adotada para o desenvolvimento das atividades de identificação, arranjo e descrição dos documentos arquivísticos de guarda permanente.\n\nCondicional "Até a conclusão da organização" convencionado para 2 anos no corrente, que foi considerado como tempo médio para concluir a organização.','069.11 06911 arranjo e descricao metodologia adotada para o desenvolvimento das atividades de identificacao arranjo e descricao dos documentos arquivisticos de guarda permanente eliminar 2 anos apos a conclusao da organizacao as versoes preliminares das planilhas e dos estudos de apoio incluem-se documentos referentes a metodologia adotada para o desenvolvimento das atividades de identificacao arranjo e descricao dos documentos arquivisticos de guarda permanente condicional ate a conclusao da organizacao convencionado para 2 anos no corrente que foi considerado como tempo medio para concluir a organizacao','S','2','2','G','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'069.12','ELABORAÇÃO DE INSTRUMENTOS DE PESQUISA (instrumentos que permitem a identificação e a localização dos documentos arquivísticos de guarda permanente: catálogo, guia, inventário, listagem descritiva do acervo, repertório e tabela de equivalência)','Eliminar, 2 anos após a finalização da elaboração, as versões preliminares dos instrumentos assim como os demais exemplares quando ocorrerem atualizações.\n\nCondicional "Até a finalização da elaboração dos instrumentos de pesquisa" convencionado para 2 anos no corrente, que foi considerado como tempo médio para finalização.','069.12 06912 elaboracao de instrumentos de pesquisa instrumentos que permitem a identificacao e a localizacao dos documentos arquivisticos de guarda permanente: catalogo guia inventario listagem descritiva do acervo repertorio e tabela de equivalencia eliminar 2 anos apos a finalizacao da elaboracao as versoes preliminares dos instrumentos assim como os demais exemplares quando ocorrerem atualizacoes condicional ate a finalizacao da elaboracao dos instrumentos de pesquisa convencionado para 2 anos no corrente que foi considerado como tempo medio para finalizacao','S','5','2','G','N','2');
