INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'069.3','PUBLICAÇÃO DE MATÉRIAS (publicação de matérias, da competência do órgão e entidade, em diários oficiais e em periódicos de grande circulação)','Quanto à publicação de matérias nos boletins administrativos e de serviço, classificar no código 010.01.\nQuanto à publicação de matérias nos boletins de pessoal, classificar no código 020.01.\n\nCondicional "Até a aprovação das contas pelo Tribunal de Contas" convencionado para 5 anos no corrente e "5 anos a contar da aprovação das contas pelo Tribunal de Contas" para 15 anos no intermediário, somando-se 20 anos.','069.3 0693 publicacao de materias publicacao de materias da competencia do orgao e entidade em diarios oficiais e em periodicos de grande circulacao quanto a publicacao de materias nos boletins administrativos e de servico classificar no codigo 01001 quanto a publicacao de materias nos boletins de pessoal classificar no codigo 02001 condicional ate a aprovacao das contas pelo tribunal de contas convencionado para 5 anos no corrente e 5 anos a contar da aprovacao das contas pelo tribunal de contas para 15 anos no intermediario somando-se 20 anos','S','15','5','E','N','2');
INSERT INTO seq_assunto (campo) VALUES (0); INSERT INTO assunto VALUES (LAST_INSERT_ID(),'070','GESTÃO DOS SERVIÇOS POSTAIS E DE TELECOMUNICAÇÕES','Esta subclasse contempla documentos referentes às atividades desenvolvidas pelo órgão e entidade para a gestão dos serviços postais e de telecomunicações (radiofrequência, telex, telefonia, fax e transmissão de dados, voz e imagem), bem como aqueles referentes à contratação de prestação de serviços de instalação, manutenção e reparo e à autorização e controle de uso.','070 070 gestao dos servicos postais e de telecomunicacoes esta subclasse contempla documentos referentes as atividades desenvolvidas pelo orgao e entidade para a gestao dos servicos postais e de telecomunicacoes radiofrequencia telex telefonia fax e transmissao de dados voz e imagem bem como aqueles referentes a contratacao de prestacao de servicos de instalacao manutencao e reparo e a autorizacao e controle de uso','S',NULL,NULL,NULL,'S','2');
